import React, { useState, useEffect } from 'react';

const SobelOperatorAnimation = () => {
  const gridSize = 10;
  const [step, setStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [mathExplanation, setMathExplanation] = useState('');
  const [grid, setGrid] = useState(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
  const [edgeGrid, setEdgeGrid] = useState(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
  const [speed, setSpeed] = useState(1000); // 1000ms = 1 segundo por defecto

  const sobelX = [[-1, 0, 1],[-2, 0, 2],[-1, 0, 1]];
  const sobelY = [[-1, -2, -1],[0, 0, 0],[1, 2, 1]];

  useEffect(() => {
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        setStep((prevStep) => {
          const newStep = (prevStep + 1) % ((gridSize - 2) * (gridSize - 2));
          updateEdgeGrid(newStep);
          return newStep;
        });
      }, speed);
    }
    return () => clearInterval(interval);
  }, [isPlaying, speed]);

  const applySobel = (x, y) => {
    let gx = 0, gy = 0;
    let explanation = "Explicación del Operador Sobel:\n\n";
    explanation += "1. Kernels del operador Sobel:\n";
    explanation += "   Gx (bordes verticales):    Gy (bordes horizontales):\n";
    explanation += "   [-1  0  1]                 [-1 -2 -1]\n";
    explanation += "   [-2  0  2]                 [ 0  0  0]\n";
    explanation += "   [-1  0  1]                 [ 1  2  1]\n\n";

    explanation += "2. Valores de píxeles (3x3):\n";
    let pixelValues = [];
    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
        const pixelValue = grid[x + i - 1][y + j - 1];
        pixelValues.push(pixelValue);
      }
    }
    explanation += `   [${pixelValues[0]} ${pixelValues[1]} ${pixelValues[2]}]\n`;
    explanation += `   [${pixelValues[3]} ${pixelValues[4]} ${pixelValues[5]}]\n`;
    explanation += `   [${pixelValues[6]} ${pixelValues[7]} ${pixelValues[8]}]\n\n`;

    explanation += "3. Cálculo de Gx y Gy:\n";
    for (let i = 0; i < 3; i++) {
      for (let j = 0; j < 3; j++) {
        gx += pixelValues[i*3 + j] * sobelX[i][j];
        gy += pixelValues[i*3 + j] * sobelY[i][j];
      }
    }
    explanation += `   Gx = ${gx.toFixed(2)}\n`;
    explanation += `   Gy = ${gy.toFixed(2)}\n\n`;

    const magnitude = Math.sqrt(gx * gx + gy * gy);
    explanation += "4. Magnitud del gradiente:\n";
    explanation += `   √(Gx² + Gy²) = √(${gx.toFixed(2)}² + ${gy.toFixed(2)}²) = ${magnitude.toFixed(2)}\n\n`;

    explanation += "5. Interpretación:\n";
    if (magnitude > 2) explanation += "   Borde fuerte detectado.\n";
    else if (magnitude > 1) explanation += "   Borde moderado detectado.\n";
    else explanation += "   Área relativamente uniforme.\n";

    setMathExplanation(explanation);
    return magnitude;
  };

  const updateEdgeGrid = (currentStep) => {
    const x = Math.floor(currentStep / (gridSize - 2)) + 1;
    const y = (currentStep % (gridSize - 2)) + 1;
    const newEdgeGrid = [...edgeGrid];
    newEdgeGrid[x][y] = applySobel(x, y);
    setEdgeGrid(newEdgeGrid);
  };

  const handleCellClick = (x, y) => {
    const newGrid = [...grid];
    newGrid[x][y] = 1 - newGrid[x][y]; // Toggle between 0 and 1
    setGrid(newGrid);
    setEdgeGrid(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
    setStep(0);
    setIsPlaying(false);
  };

  const renderGrid = (gridToRender, isEdgeGrid = false, isClickable = false) => {
    const currentX = Math.floor(step / (gridSize - 2)) + 1;
    const currentY = (step % (gridSize - 2)) + 1;

    return gridToRender.map((row, x) => (
      <div key={x} className="flex">
        {row.map((cell, y) => {
          const isCurrent = x === currentX && y === currentY;
          const isKernel = x >= currentX - 1 && x <= currentX + 1 && y >= currentY - 1 && y <= currentY + 1;
          let cellClass = "w-8 h-8 border border-gray-300 flex items-center justify-center text-xs";
          if (isEdgeGrid) {
            const intensity = Math.min(255, Math.round(cell * 100));
            cellClass += ` bg-gray-${Math.floor(intensity / 25) * 100}`;
          } else {
            if (isCurrent) cellClass += " bg-red-500";
            else if (isKernel) cellClass += " bg-yellow-200";
            else cellClass += cell ? " bg-black" : " bg-white";
          }
          return (
            <div 
              key={y} 
              className={cellClass}
              onClick={() => isClickable && handleCellClick(x, y)}
              style={isClickable ? {cursor: 'pointer'} : {}}
            >
              {isEdgeGrid && cell > 0 && cell.toFixed(1)}
            </div>
          );
        })}
      </div>
    ));
  };

  const changeSpeed = (delta) => {
    setSpeed(prevSpeed => Math.max(100, Math.min(2000, prevSpeed + delta)));
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Animación Interactiva del Operador Sobel</h2>
      <div className="flex mb-4">
        <div className="mr-4">
          <h3 className="text-lg font-semibold mb-2">Imagen Original (Haz clic para dibujar)</h3>
          {renderGrid(grid, false, true)}
        </div>
        <div>
          <h3 className="text-lg font-semibold mb-2">Bordes Detectados</h3>
          {renderGrid(edgeGrid, true)}
        </div>
        <div className="ml-4 w-96">
          <h3 className="text-lg font-semibold mb-2">Explicación Detallada</h3>
          <pre className="bg-gray-100 p-2 rounded text-xs whitespace-pre-wrap overflow-auto h-80">
            {mathExplanation}
          </pre>
        </div>
      </div>
      <div className="mb-4">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2"
          onClick={() => setIsPlaying(!isPlaying)}
        >
          {isPlaying ? 'Pausar' : 'Reproducir'}
        </button>
        <button
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded mr-2"
          onClick={() => {
            setStep(0);
            setIsPlaying(false);
            setGrid(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
            setEdgeGrid(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
            setMathExplanation('');
          }}
        >
          Reiniciar
        </button>
        <button
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded mr-2"
          onClick={() => changeSpeed(-100)}
        >
          Acelerar
        </button>
        <button
          className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded mr-2"
          onClick={() => changeSpeed(100)}
        >
          Desacelerar
        </button>
        <span className="text-sm">Velocidad actual: {speed}ms por paso</span>
      </div>
    </div>
  );
};

export default SobelOperatorAnimation;
