import React, { useState, useEffect } from 'react';

const SobelOperatorApp = () => {
  const [activeSection, setActiveSection] = useState('animacion');
  const gridSize = 10;
  const [step, setStep] = useState(0);
  const [isPlaying, setIsPlaying] = useState(false);
  const [mathExplanation, setMathExplanation] = useState('');
  const [grid, setGrid] = useState(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
  const [edgeGrid, setEdgeGrid] = useState(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
  const [speed, setSpeed] = useState(1000);

  const sobelX = [[-1, 0, 1],[-2, 0, 2],[-1, 0, 1]];
  const sobelY = [[-1, -2, -1],[0, 0, 0],[1, 2, 1]];

  useEffect(() => {
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        setStep((prevStep) => {
          const newStep = (prevStep + 1) % ((gridSize - 2) * (gridSize - 2));
          updateEdgeGrid(newStep);
          return newStep;
        });
      }, speed);
    }
    return () => clearInterval(interval);
  }, [isPlaying, speed]);

  const applySobel = (x, y) => {
    // ... (el código de applySobel permanece igual)
  };

  const updateEdgeGrid = (currentStep) => {
    // ... (el código de updateEdgeGrid permanece igual)
  };

  const handleCellClick = (x, y) => {
    // ... (el código de handleCellClick permanece igual)
  };

  const renderGrid = (gridToRender, isEdgeGrid = false, isClickable = false) => {
    // ... (el código de renderGrid permanece igual)
  };

  const changeSpeed = (delta) => {
    setSpeed(prevSpeed => Math.max(100, Math.min(2000, prevSpeed + delta)));
  };

  const renderTeoria = () => (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold mb-4">Teoría del Operador Sobel</h3>
      <p>El operador Sobel es una técnica utilizada en procesamiento de imágenes para la detección de bordes. Funciona calculando el gradiente de la intensidad de la imagen en cada punto, dando la dirección del mayor incremento posible de claro a oscuro y la tasa de cambio en esa dirección.</p>
      <p className="mt-4">Los componentes principales del operador Sobel son:</p>
      <ul className="list-disc list-inside mt-2">
        <li>Dos kernels de convolución 3x3</li>
        <li>Cálculo de gradientes en direcciones x e y</li>
        <li>Combinación de gradientes para obtener la magnitud total</li>
      </ul>
      {/* Añade más contenido teórico aquí */}
    </div>
  );

  const renderAnimacion = () => (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold mb-4">Animación del Operador Sobel</h3>
      <div className="flex flex-col md:flex-row mb-4">
        <div className="md:w-1/3 mb-4 md:mb-0 md:mr-4">
          <h4 className="text-lg font-semibold mb-2">Imagen Original (Haz clic para dibujar)</h4>
          {renderGrid(grid, false, true)}
        </div>
        <div className="md:w-1/3 mb-4 md:mb-0 md:mr-4">
          <h4 className="text-lg font-semibold mb-2">Bordes Detectados</h4>
          {renderGrid(edgeGrid, true)}
        </div>
        <div className="md:w-1/3">
          <h4 className="text-lg font-semibold mb-2">Explicación Detallada</h4>
          <pre className="bg-gray-100 p-2 rounded text-xs whitespace-pre-wrap overflow-auto" style={{height: 'calc(100vh - 350px)'}}>
            {mathExplanation}
          </pre>
        </div>
      </div>
      <div className="flex flex-wrap justify-center items-center">
        <button
          className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded m-2"
          onClick={() => setIsPlaying(!isPlaying)}
        >
          {isPlaying ? 'Pausar' : 'Reproducir'}
        </button>
        <button
          className="bg-gray-500 hover:bg-gray-700 text-white font-bold py-2 px-4 rounded m-2"
          onClick={() => {
            setStep(0);
            setIsPlaying(false);
            setGrid(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
            setEdgeGrid(Array(gridSize).fill().map(() => Array(gridSize).fill(0)));
            setMathExplanation('');
          }}
        >
          Reiniciar
        </button>
        <button
          className="bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 rounded m-2"
          onClick={() => changeSpeed(-100)}
        >
          Acelerar
        </button>
        <button
          className="bg-yellow-500 hover:bg-yellow-700 text-white font-bold py-2 px-4 rounded m-2"
          onClick={() => changeSpeed(100)}
        >
          Desacelerar
        </button>
        <span className="text-sm m-2">Velocidad actual: {speed}ms por paso</span>
      </div>
    </div>
  );

  const renderEvaluacion = () => (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold mb-4">Evaluación</h3>
      <p>Aquí puedes implementar un cuestionario o ejercicios prácticos para evaluar el entendimiento del Operador Sobel.</p>
      {/* Implementa aquí tu sistema de evaluación */}
    </div>
  );

  const renderAcercaDe = () => (
    <div className="bg-white p-6 rounded-lg shadow-lg">
      <h3 className="text-xl font-bold mb-4">Acerca de</h3>
      <p>Esta aplicación fue desarrollada para ayudar a entender y visualizar el funcionamiento del Operador Sobel en el procesamiento de imágenes.</p>
      <p className="mt-4">Desarrollado por: [Tu Nombre o Equipo]</p>
      <p>Versión: 1.0</p>
      {/* Añade más información sobre la aplicación o los desarrolladores */}
    </div>
  );

  const renderContent = () => {
    switch(activeSection) {
      case 'teoria':
        return renderTeoria();
      case 'animacion':
        return renderAnimacion();
      case 'evaluacion':
        return renderEvaluacion();
      case 'acercade':
        return renderAcercaDe();
      default:
        return renderAnimacion();
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <nav className="bg-blue-600 text-white p-4">
        <ul className="flex justify-center space-x-4">
          <li>
            <button 
              className={`px-3 py-2 rounded ${activeSection === 'teoria' ? 'bg-blue-800' : 'hover:bg-blue-700'}`}
              onClick={() => setActiveSection('teoria')}
            >
              1. Teoría
            </button>
          </li>
          <li>
            <button 
              className={`px-3 py-2 rounded ${activeSection === 'animacion' ? 'bg-blue-800' : 'hover:bg-blue-700'}`}
              onClick={() => setActiveSection('animacion')}
            >
              2. Animación
            </button>
          </li>
          <li>
            <button 
              className={`px-3 py-2 rounded ${activeSection === 'evaluacion' ? 'bg-blue-800' : 'hover:bg-blue-700'}`}
              onClick={() => setActiveSection('evaluacion')}
            >
              3. Evaluación
            </button>
          </li>
          <li>
            <button 
              className={`px-3 py-2 rounded ${activeSection === 'acercade' ? 'bg-blue-800' : 'hover:bg-blue-700'}`}
              onClick={() => setActiveSection('acercade')}
            >
              4. Acerca de
            </button>
          </li>
        </ul>
      </nav>
      <main className="flex-grow p-4">
        <div className="max-w-6xl mx-auto">
          <h2 className="text-2xl font-bold mb-4 text-center">Aplicación del Operador Sobel</h2>
          {renderContent()}
        </div>
      </main>
      <footer className="bg-gray-200 text-center p-4">
        <p>&copy; 2024 Aplicación del Operador Sobel. Todos los derechos reservados.</p>
      </footer>
    </div>
  );
};

export default SobelOperatorApp;
